// =============================================================================// com.bitmovers.utilities.servlet.HTMLResultSetTable// =============================================================================package com.bitmovers.utilities.servlet;import java.io.*;import java.util.*;import java.sql.*;import com.bitmovers.utilities.*;// =============================================================================// CLASS: HTMLResultSetTester// =============================================================================/**   *  * @version 2000.05.10  * @author Ian Wojtowicz (ian@bitmovers.com)  */public class HTMLResultSetTester{  // ---------------------------------------------------------------------------  	  // ---------------------------------------------------------------------------	// METHOD: main  // ---------------------------------------------------------------------------  	public static void main(String[] arguments)	{		String databaseURL = "jdbc:mysql://localhost/webCT.certification.patrick";		String databaseDriverClass = "org.gjt.mm.mysql.Driver";		String databaseUsername = "poCore";		String databasePassword = "poCore";		String query = "select * from CUSTOMERS order by ID";		    try    {  	  Class.forName(databaseDriverClass).newInstance();  	}  	catch (Exception exception)  	{	    System.err.println("The JDBC driver (" + databaseDriverClass + ") could not be loaded.  Is the Servlet JAR file complete?");	    return;  	}  	  	Connection connection;  	try    {	  	connection = DriverManager.getConnection(databaseURL, databaseUsername, databasePassword);    }    catch(SQLException e)    {	    System.err.println("Could not get connection (" + databaseURL + ", " + databaseUsername + ", " + databasePassword + ")");	    System.err.println(e.getMessage());	    e.printStackTrace(System.err);	    return;    }		try		{	    ResultSet results = connection.createStatement().executeQuery(query);			HTMLResultSetTable htmlTable = new HTMLResultSetTable(HTMLResultSetTable.getResultSetAsHashtable(results), "ID");			System.out.println(htmlTable.getHTML());    }    catch(SQLException e)    {	    System.err.println("Could not execute query.");    }	}		  // ---------------------------------------------------------------------------}// =============================================================================// Copyright 2000 Bitmovers Communications, Inc.                             eof